    public static Texture2D BytesToTexture(GraphicsDevice graphicsDevice, byte[,] imageData)
    {
        if (imageData.GetLength(0) != 48 || imageData.GetLength(0) != imageData.GetLength(1))
            throw new InvalidOperationException("Image data must be 48x48");

        Texture2D tex = new(graphicsDevice, 48, 48);
        Color[] data = new Color[48 * 48];
        for (int y = 0; y < 48; y++)
        {
            for (int x = 0; x < 48; x++)
            {
                int i = y * 48 + x;
                data[i] = imageData[y, x] switch // image data is column-major
                {
                    0 => Color.Black,
                    1 => Color.White,
                    2 => Color.Gray,
                    _ => throw new InvalidOperationException($"invalid color byte {imageData[x, y]}"),
                };
            }
        }

        tex.SetData(data);
        return tex;
    }
